import { JsonObject } from 'type-fest';
import { JSONSchema4 } from 'json-schema';
export type JsonSchema = JSONSchema4;
export type RecordType = JsonObject;
export type SdmSchemaAppsDbValue = {
    jsonSchema: JsonSchema;
    displayFieldName: string;
};
export type SdmSchema = SdmSchemaAppsDbValue & {
    id: string;
};
export type SdmRecordAppsDbValue<T extends RecordType> = {
    sdmSchemaId: string;
    record: T;
};
export type SdmRecord<T extends RecordType = RecordType> = T & {
    id: string;
    sdmStoreId: string;
    sdmSchemaId: string;
};
export type SdmContentStore = {
    id: string;
    value: boolean;
};
